drop table if exists livro;
drop table if exists editora;

create table editora(
	id serial primary key,
	nome varchar(60)
);

create table livro(
	id serial primary key,
	id_editora int REFERENCES editora(id),
	titulo varchar(120),
	autor varchar (80),
	ano int,
	preco real,
	quantidade int	
);



	
